import './Style.css';
import React, { useState, useRef, useEffect } from "react";
import heart from './gameheart.png'
import triangle from './triangle1.png'







const Main = () => {

const [shapeState, setShapeState] = useState(33)

const gridSquares = () => {
    let gridArray = []
    for(let i = 0; i < 100; i++){
        if (i === shapeState){
            gridArray.push(<div key="grid-{i}" id ="shape" className="grid-item"><img src={triangle}></img></div>)
        }
        else if (i % 5 === 0 && i % 10 !==0 && i !==55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item y-axis">{(i+5)/(-10)+6}</div>)
        }
        else if (i >49 && i <60 && i !==55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item x-axis">{i-55}</div>)
        }     
        else if (i===55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item x-axis y-axis">{i-55}</div>)
        } else {
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item">{i}</div>)
        }


    }
    return gridArray
}

const move = () => {
    setShapeState(50)
}






    return (
        <div className='main'>
        <div className="main-grid">
            {gridSquares()}

            </div>     
            <button onClick={move}>MOVE</button>       
        </div>
    )

}

export default Main
