import './Style.css';
import React, { useState, useRef, useEffect } from "react";
import heart from './gameheart.png'







const Main = () => {

const gridSquares = () => {
    let gridArray = []
    for(let i = 0; i < 100; i++){
        if (i % 5 === 0 && i % 10 !==0 && i !==55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item y-axis">{(i+5)/(-10)+6}</div>)
        }
        else if (i >49 && i <60 && i !==55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item x-axis">{i-55}</div>)
        }     
        else if (i===55){
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item x-axis y-axis">{i-55}</div>)
        } else {
            gridArray.push(<div key="grid-{i}" id ="grid-{i}" className="grid-item"></div>)
        }


    }
    return gridArray
}






    return (
        <div className='main'>
        <div className="main-grid">
            {gridSquares()}

            </div>            
        </div>
    )

}

export default Main
